// Declare internals

const internals = {};


exports = module.exports = internals.jQuerySnippetGenerator = function () { };


internals.jQuerySnippetGenerator.generate = function (clientId) {

    return [
        'jQuery.ajaxSetup({',
        '    beforeSend: function (xhr, settings) {',
        '        var clientId = \'' + clientId + '\';',
        '        settings.url += ((settings.url.indexOf(\'?\') !== -1) ? \'&\' : \'?\') + \'debug=\' + clientId;',
        '    }',
        '});'
    ].join('\n');
};

'use strict';
// Load modules

const _ = require('lodash');


// Declare internals

const internals = {
    requests: {
        prefix: '',
        separator: '============='
    },
    serverLogs: {
        prefix: '  ',
        separator: '  -------------'
    }
};


exports = module.exports = internals.RequestToTextConverter = function (request) {

    this._request = request;
};


internals.RequestToTextConverter.create = function (request) {

    return new internals.RequestToTextConverter(request);
};


internals.RequestToTextConverter.convertToText = function (request) {

    return internals.RequestToTextConverter.create(request).convertToText();
};


internals.RequestToTextConverter.prototype.convertToText = function () {

    return [this._buildRequestText(), this._buildServerLogsText()].join('\n\n');
};


internals.RequestToTextConverter.prototype._buildRequestText = function () {

    return [
        ['Path:', this._request.method.toUpperCase(), this._request.path].join(' '),
        ['Status:', this._request.statusCode].join(' '),
        ['Server Logs:']
    ].join('\n');
};


internals.RequestToTextConverter.prototype._buildServerLogsText = function () {

    return _.map(this._request.serverLogs, this._buildServerLogText, this).join('\n' + internals.serverLogs.separator + '\n');
};


internals.RequestToTextConverter.prototype._buildServerLogText = function (serverLog) {

    return [
        internals.serverLogs.prefix + ['Tags:', JSON.stringify(serverLog.tags)].join(' '),
        internals.serverLogs.prefix + ['Timestamp:', serverLog.timestamp].join(' '),
        internals.serverLogs.prefix + ['Data:', JSON.stringify(serverLog.data)].join(' ')
    ].join('\n');
};

'use strict';
// Load modules

const Backbone = require('backbone');

const ToolbarView = require('./toolbar');
const FeedHeaderView = require('./feedHeader');
const FeedBodyView = require('./feedBody');
const SettingsView = require('./settings');
const Settings = require('../models/settings');
const SettingsStore = require('../settingsStore');
const ClientIdGenerator = require('../utils/clientIdGenerator');


// Declare internals

const internals = {};


exports = module.exports = internals.AppView = Backbone.View.extend({

    template: require('../templates/app.hbs'),

    initialize: function (opts) {

        this._webSocketManager = opts.webSocketManager;

        this.model = new Settings(null, {
            webSocketManager: opts.webSocketManager
        });
    },

    render: function () {

        const $markup = $(this.template());

        this._renderChildViews($markup);

        this.$el.html($markup);

        return this;
    },

    _renderChildViews: function ($markup) {

        const settingsView = this._renderSettings($markup);
        const toolbarView = this._renderToolbar($markup);
        const feedHeaderView = this._renderFeedHeader($markup);
        const feedBodyView = this._renderFeedBody($markup);

        this.listenTo(toolbarView, 'pause', this._webSocketManager.pause.bind(this._webSocketManager));
        this.listenTo(toolbarView, 'resume', this._webSocketManager.resume.bind(this._webSocketManager));
        this.listenTo(toolbarView, 'searchChanged', feedBodyView.filterRequests.bind(feedBodyView));
        this.listenTo(toolbarView, 'showSettings', settingsView.show.bind(settingsView));
        this.listenTo(toolbarView, 'clearFeed', function () {

            this._handleClearFeed(feedHeaderView, feedBodyView);
        });

        this.listenTo(feedHeaderView, 'toggleFavorites', feedBodyView.toggleFavorites.bind(feedBodyView));
        this.listenTo(feedHeaderView, 'collapseAll', feedBodyView.collapseAll.bind(feedBodyView));

        this.listenTo(feedBodyView, 'requestExpandToggle', function (expanded) {

            this._handleRequestExpandToggle(expanded, feedHeaderView, feedBodyView);
        });

        this.listenTo(feedBodyView, 'requestFavoriteToggle', function (favorited) {

            this._handleRequestFavoriteToggle(favorited, feedHeaderView, feedBodyView);
        });
    },

    _renderToolbar: function ($markup) {

        return new ToolbarView({
            el: $markup.siblings('.toolbar'),
            model: this.model
        }).render();
    },

    _renderSettings: function ($markup) {

        const settingsView = new SettingsView({
            el: $markup.siblings('.settings-modal-container'),
            settingsModel: this.model
        });

        if (this._firstVisit()) {
            this.model.set('clientId', ClientIdGenerator.generate());
            this.model.set('channel', '*');
            settingsView.render().show();
        }
        else {
            settingsView.render();
        }

        return settingsView;
    },

    _firstVisit: function (){

        return !SettingsStore.exists('channel');
    },

    _renderFeedHeader: function ($markup) {

        return new FeedHeaderView({
            el: $markup.find('.header')
        }).render();
    },

    _renderFeedBody: function ($markup) {

        return new FeedBodyView({
            el: $markup.find('.body'),
            collection: this.collection
        }).render();
    },


    _handleRequestExpandToggle: function (expanded, feedHeaderView, feedBodyView) {

        if (!expanded && !feedBodyView.hasExpandedRequests()) {
            feedHeaderView.disableCollapseAll();
        }
        else if (expanded) {
            feedHeaderView.enableCollapseAll();
        }
    },

    _handleRequestFavoriteToggle: function (favorited, feedHeaderView, feedBodyView) {

        if (!favorited && !feedBodyView.hasFavoritedRequests()) {
            feedHeaderView.disableFavoritesFilter();
        }
        else if (favorited) {
            feedHeaderView.enableFavoritesFilter();
        }
    },

    _handleClearFeed: function (feedHeaderView, feedBodyView) {

        feedHeaderView.clear();
        feedBodyView.clear();
    }

});

'use strict';
// Load modules

const Backbone = require('backbone');
const _ = require('lodash');

const ChannelSelectorView = require('./channelSelector');


// Declare internals

const internals = {};


exports = module.exports = internals.ToolbarView = Backbone.View.extend({

    template: require('../templates/toolbar.hbs'),

    events: {
        'keyup .search': '_triggerSearchChanged',
        'click .settings': '_triggerShowSettings',
        'click .clear': '_triggerClearFeed',
        'click .pause-resume': '_pauseResumeRequests'
    },

    render: function () {

        this.$el.html(this.template());

        new ChannelSelectorView({ el: this.$('.channel-selector-container'), model: this.model }).render();

        return this;
    },

    _pauseResumeRequests: function (e) {

        const paused = $(e.currentTarget).find('.resume:visible').length === 1;

        if (paused) {
            this._resumeRequests();
        }
        else {
            this._pauseRequests();
        }
    },

    _pauseRequests: function () {

        this.$el.find('.pause').addClass('hidden');
        this.$el.find('.resume').removeClass('hidden');
        this.trigger('pause');
    },

    _resumeRequests: function () {

        this.$el.find('.pause').removeClass('hidden');
        this.$el.find('.resume').addClass('hidden');
        this.trigger('resume');
    },

    _triggerSearchChanged: _.debounce(function (e) {

        const queryString = $(e.currentTarget).val();
        this.trigger('searchChanged', queryString);
    }, 200),

    _triggerShowSettings: function () {

        this.trigger('showSettings');
    },

    _triggerClearFeed: function () {

        this.trigger('clearFeed');
    }

});

