const Backbone = require('backbone');


// Declare internals

const internals = {};


exports = module.exports = internals.FeedHeaderView = Backbone.View.extend({

    template: require('../templates/feedHeader.hbs'),

    events: {
        'click .expander': '_collapseAll',
        'click .favorite.enabled': '_applyFavoritesFilter',
        'click .favorite.active': '_removeFavoritesFilter'
    },

    render: function () {

        this.$el.html(this.template());

        return this;
    },

    clear: function () {

        this.disableCollapseAll();
        this.disableFavoritesFilter();
    },

    enableCollapseAll: function () {

        this.$('.expander').addClass('expanded');
    },

    disableCollapseAll: function () {

        this.$('.expander').removeClass('expanded');
    },

    enableFavoritesFilter: function () {

        this.$('.favorite').addClass('enabled');
    },

    disableFavoritesFilter: function () {

        this.$('.favorite')
            .removeClass('enabled')
            .removeClass('active')
            .addClass('empty');

        this.trigger('toggleFavorites', false);
    },

    _collapseAll: function () {

        this.$('.expander').removeClass('expanded');

        this.trigger('collapseAll');
    },

    _applyFavoritesFilter: function (e) {

        $(e.currentTarget)
            .removeClass('enabled')
            .addClass('active');

        this.trigger('toggleFavorites', true);
    },

    _removeFavoritesFilter: function (e) {

        $(e.currentTarget)
            .removeClass('active')
            .addClass('enabled');

        this.trigger('toggleFavorites', false);
    }

});

'use strict';
// Load modules

const Backbone = require('backbone');
const _ = require('lodash');

const RequestView = require('./request');
const SearchCriteria = require('../utils/search/searchCriteria');


// Declare internals

const internals = {};


exports = module.exports = internals.FeedBodyView = Backbone.View.extend({

    _requestViews: [],
    _filterFavorites: false,

    initialize: function (options) {

        this.listenTo(this.collection, 'add', this._addRequest);
    },

    render: function () {

        this.clear();

        return this;
    },

    clear: function () {

        this.$el.empty();

        _.each(this._requestViews, (requestView) => {

            requestView.remove();
        });

        this._requestViews = [];
    },

    hasFavoritedRequests: function () {

        return _.some(this._requestViews, (requestView) => {

            return requestView.favorited;
        });
    },

    hasExpandedRequests: function () {

        return _.some(this._requestViews, (requestView) => {

            return requestView.active;
        });
    },

    collapseAll: function () {

        this.$('.request.active').removeClass('active');
        this.$('.request .server-logs').hide();

        _.each(this._requestViews, (requestView) => {

            requestView.active = false;
        });
    },

    toggleFavorites: function (toggle) {

        this._filterFavorites = toggle;
        this._refreshRequestsVisibility();
    },

    filterRequests: function (queryString) {

        const searchCriteria = SearchCriteria.create(queryString);
        this.searchFilter = function (requestView) {

            return searchCriteria.matches(requestView.model.toJSON());
        };

        this._refreshRequestsVisibility();
    },

    _refreshRequestsVisibility: function () {

        _.each(this._requestViews, this._updateRequestVisibility.bind(this));
    },

    _addRequest: function (request) {

        const requestView = new RequestView({ model: request }).render();
        this._requestViews.push(requestView);

        this.listenTo(requestView, 'serverLogsToggle', (expanded) => {

            this.trigger('requestExpandToggle', expanded);
        });

        this.listenTo(requestView, 'favoriteToggle', (favorited) => {

            this.trigger('requestFavoriteToggle', favorited);

            if (!favorited && self._filterFavorites) {
                requestView.toggleVisibility(false);
            }
        });

        this._updateRequestVisibility(requestView);

        this.listenTo(request, 'change:statusCode', () => {

            this._updateRequestVisibility(requestView, true);
        });

        this._checkToScrollToBottom(() => {

            this.$el.append(requestView.$el);
        });
    },

    _checkToScrollToBottom: function (domManipulationFn) {

        const isScrolledToBottom = this._isScrolledToBottom();

        domManipulationFn();

        if (isScrolledToBottom) {
            this._scrollToBottom();
        }
    },

    _updateRequestVisibility: function (requestView, isUpdate) {

        let show = true;

        if (this.searchFilter && !this.searchFilter(requestView)) {
            show = false;
        }

        if (this._filterFavorites && !requestView.favorited) {
            show = false;
        }

        requestView.toggleVisibility(show);
    },

    _isScrolledToBottom: function () {

        return ((window.innerHeight + window.scrollY) >= document.body.offsetHeight);
    },

    _scrollToBottom: function () {

        window.scrollTo(0, document.body.scrollHeight);
    }

});

'use strict';
// Load modules

const Backbone = require('backbone');

const ServerLogsView = require('./serverLogs');
const RequestDetailsView = require('./requestDetails');


// Declare internals

const internals = {};


exports = module.exports = internals.RequestView = Backbone.View.extend({

    template: require('../templates/request.hbs'),

    className: 'request hidden',

    events: {
        'click .request-details': '_toggleServerLogs',
        'click .favorite': '_toggleFavorite'
    },

    favorited: false,
    active: false,
    visible: false,

    render: function () {

        const $markup = $(this.template());

        new RequestDetailsView({
            el: $markup.siblings('.request-details'),
            model: this.model }).render();

        $markup.siblings('.server-logs').hide();

        this.$el.html($markup);

        return this;
    },

    toggleVisibility: function (visible) {

        this.visible = visible;
        this.$el.toggleClass('hidden', !visible);
    },

    _toggleServerLogs: function () {

        this.active = !this.active;

        this.$el.toggleClass('active', this.active);

        if (!this.serverLogsView) {
            this.serverLogsView = new ServerLogsView({
                el: this.$('.server-logs'),
                model: this.model,
                collection: this.model.get('serverLogs')
            }).render();
        }

        if (this.active) {
            this.serverLogsView.$el.show();
        }
        else if (this.serverLogsView) {
            this.serverLogsView.$el.hide();
        }

        this.trigger('serverLogsToggle', this.active);
    },

    _toggleFavorite: function (e) {

        e.stopPropagation();

        this.favorited = !this.favorited;

        this.$('.favorite').toggleClass('active', this.favorited);

        this.trigger('favoriteToggle', this.favorited);
    }

});

'use strict';
// Load modules

const Backbone = require('backbone');
const ZeroClipboard = require('zeroclipboard');

const RequestToTextConverter = require('../utils/requestToTextConverter');


// Declare internals

const internals = {};


exports = module.exports = internals.ServerLogsView = Backbone.View.extend({

    template: require('../templates/serverLogs.hbs'),

    events: {
        'click .json-markup': '_toggleServerLogData'
    },

    initialize: function () {

        this.listenTo(this.collection, 'add', (model) => {

            this.render();
        });
    },

    render: function () {

        this.$el.html(this.template(this.collection.toJSON()));

        this._initializeClipboard();

        return this;
    },

    _initializeClipboard: function () {

        const clipboard = this._createZeroClipboard();

        clipboard.on('ready', (readyEvent) => {

            clipboard.on('beforecopy', (event) => {

                clipboard.setData('text/plain', RequestToTextConverter.convertToText(this.model.toJSON()));

                this._$clipboard.tooltip({
                    delay: { hide: 2000 },
                    placement: 'left',
                    animation: 'fade',
                    title: 'Copied to clipboard!'
                });

                this._$clipboard.tooltip('show');
            });

            clipboard.on('aftercopy', (event) => {

                setTimeout(() => {

                    this._$clipboard.tooltip('hide');
                }, 3000);
            });

        });
    },

    _createZeroClipboard: function () {

        this._$clipboard = this.$('.copy');

        ZeroClipboard.config({
            swfPath: location.href + '/js/ZeroClipboard.swf'
        });

        return new ZeroClipboard(this._$clipboard.get(0));
    },

    _toggleServerLogData: function (e) {

        const $data = $(e.currentTarget);

        $data.closest('.data').toggleClass('expanded');
    }

});

